<div class="container">
  <h2>Add Waiter</h2>
  <form (ngSubmit)="onSubmit()" #addWaiterForm="ngForm">
    <div class="form-group">
      <label for="username">Username</label>
      <input type="text" class="form-control" id="username" [(ngModel)]="waiter.username" name="username" required>
    </div>

    <div class="form-group">
      <label for="password">Password</label>
      <input type="password" class="form-control" id="password" [(ngModel)]="waiter.password" name="password" required>
    </div>

    <div class="form-group">
      <label for="confirmPassword">Confirm Password</label>
      <input type="password" class="form-control" id="confirmPassword" [(ngModel)]="waiter.confirmPassword" name="confirmPassword" required>
    </div>

    <div class="form-group">
      <label for="securityQuestion">Security Question</label>
      <input type="text" class="form-control" id="securityQuestion" [(ngModel)]="waiter.securityQuestion" name="securityQuestion" required>
    </div>

    <div class="form-group">
      <label for="securityAnswer">Security Answer</label>
      <input type="text" class="form-control" id="securityAnswer" [(ngModel)]="waiter.securityAnswer" name="securityAnswer" required>
    </div>

    <div class="form-group">
      <label for="firstName">First Name</label>
      <input type="text" class="form-control" id="firstName" [(ngModel)]="waiter.firstName" name="firstName" required>
    </div>

    <div class="form-group">
      <label for="lastName">Last Name</label>
      <input type="text" class="form-control" id="lastName" [(ngModel)]="waiter.lastName" name="lastName" required>
    </div>

    <div class="form-group">
      <label for="gender">Gender</label>
      <select class="form-control" id="gender" [(ngModel)]="waiter.gender" name="gender" required>
        <option value="M">Male</option>
        <option value="F">Female</option>
      </select>
    </div>

    <div class="form-group">
      <label for="address">Address</label>
      <input type="text" class="form-control" id="address" [(ngModel)]="waiter.address" name="address" required>
    </div>

    <div class="form-group">
      <label for="phone">Phone</label>
      <input type="text" class="form-control" id="phone" [(ngModel)]="waiter.phone" name="phone" required>
    </div>

    <div class="form-group">
      <label for="email">Email</label>
      <input type="email" class="form-control" id="email" [(ngModel)]="waiter.email" name="email" required>
    </div>

    <div class="form-group">
      <label for="profilePicture">Profile Picture</label>
      <input type="file" class="form-control" id="profilePicture" (change)="onFileChange($event)" name="profilePicture">
      <div *ngIf="imageError" class="text-danger">
        {{ imageError }}
      </div>
    </div>

    <div class="form-group">
      <label for="creditCard">Credit Card</label>
      <input type="text" class="form-control" id="creditCard" [(ngModel)]="waiter.creditCard" name="creditCard" required>
    </div>

    <div class="form-group">
      <label for="restaurant">Restaurant</label>
      <select class="form-control" id="restaurant" [(ngModel)]="restaurantId" name="restaurantId" required>
        <option *ngFor="let restaurant of restaurants" [value]="restaurant.restaurantId">{{ restaurant.name }}</option>
      </select>
    </div>

    <button type="submit" class="btn btn-primary" [disabled]="addWaiterForm.invalid || imageError || profilePictureInvalid">Add Waiter</button>
  </form>

  <h2 class="mt-5">All Waiters</h2>
  <table class="table table-striped">
    <thead>
      <tr>
        <th>Username</th>
        <th>First Name</th>
        <th>Last Name</th>
        <th>Email</th>
        <th>Restaurant</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let waiter of waiters">
        <td>{{ waiter.username }}</td>
        <td>{{ waiter.firstName }}</td>
        <td>{{ waiter.lastName }}</td>
        <td>{{ waiter.email }}</td>
        <td>{{ waiter.restaurantName }}</td>
      </tr>
    </tbody>
  </table>
</div>
