import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent {
  activeButton: string = '';

  constructor(private router: Router) {}

  selectComponent(component: string): void {
    if (this.activeButton === component) {
      this.activeButton = '';
      this.router.navigate(['/admin']);
    } else {
      this.activeButton = component;
      this.router.navigate([`/admin/${component}`]);
    }
  }
}
<!-- admin-dashboard.component.html or similar -->
<header class="d-flex justify-content-between align-items-center">
  <div class="container">
    <div class="row g-3">
      <div class="website-logo col-md-10">
        <img src="../../../assets/logo.png" alt="Website Logo">
        <h1 class="h2 mb-0" style="font-family: MilestoneBrush; margin-top: 20px;">Kutak dobre hrane</h1>
        <h1 class="h2 mb-0" style="font-family: MilestoneBrush; margin-top: 20px;">Admin component</h1>

      </div>
    </div>
    <nav class="mt-3">
      <button class="btn btn-outline-primary me-2" [ngClass]="{'active': activeButton === 'registration-requests'}" (click)="selectComponent('registration-requests')">Registration Requests</button>
      <button class="btn btn-outline-primary me-2" [ngClass]="{'active': activeButton === 'add-restaurant'}" (click)="selectComponent('add-restaurant')">Add Restaurant</button>
      <button class="btn btn-outline-primary" [ngClass]="{'active': activeButton === 'add-waiter'}" (click)="selectComponent('add-waiter')">Add Waiter</button>
    </nav>
  </div>
</header>

<div class="container mt-4">
  <router-outlet></router-outlet>
</div>
