import { Component } from '@angular/core';
import { UserService } from '../../service/user.service';
@Component({
  selector: 'app-admin-registration-requests',
  templateUrl: './admin-registration-requests.component.html',
  styleUrls: ['./admin-registration-requests.component.css']
})
export class AdminRegistrationRequestsComponent {
  registrationRequests: any[] = [];

  constructor(private adminService: UserService) { }

  ngOnInit(): void {
    this.loadRegistrationRequests();
  }

  loadRegistrationRequests(): void {
    this.adminService.getRegistrationRequests().subscribe(
      data => {
        this.registrationRequests = data;
      },
      error => {
        console.error('Error loading registration requests', error);
      }
    );
  }

  approveRequest(id: string): void {
    this.adminService.approveRequest(id).subscribe(
      () => {
        this.loadRegistrationRequests(); // Reload requests after approval
      },
      error => {
        console.error('Error approving request', error);
      }
    );
  }

  rejectRequest(id: string): void {
    this.adminService.rejectRequest(id).subscribe(
      () => {
        this.loadRegistrationRequests(); // Reload requests after rejection
      },
      error => {
        console.error('Error rejecting request', error);
      }
    );
  }
}
