import { Component } from '@angular/core';

@Component({
  selector: 'app-home-page-unregistred',
  templateUrl: './home-page-unregistred.component.html',
  styleUrls: ['./home-page-unregistred.component.css']
})
export class HomePageUnregistredComponent {
  totalRestaurants: number = 0;
  totalGuests: number = 0;
  reservationsLast24Hours: number = 0;
  reservationsLast7Days: number = 0;
  reservationsLast30Days: number = 0;
  restaurants: any[] = [];

  searchName: string = '';
  searchAddress: string = '';
  searchType: string = '';
  sortField: string = '';
  sortOrder: string = 'asc';

  constructor(private homeService: HomeService) { }

  ngOnInit(): void {
    this.getStatistics();
    this.getRestaurants();
  }

  getStatistics(): void {
    this.homeService.getStatistics().subscribe(data => {
      this.totalRestaurants = data.totalRestaurants;
      this.totalGuests = data.totalGuests;
      this.reservationsLast24Hours = data.reservationsLast24Hours;
      this.reservationsLast7Days = data.reservationsLast7Days;
      this.reservationsLast30Days = data.reservationsLast30Days;
    });
  }

  getRestaurants(): void {
    this.homeService.getRestaurants(this.searchName, this.searchAddress, this.searchType, this.sortField, this.sortOrder).subscribe(data => {
      this.restaurants = data;
    });
  }

  search(): void {
    this.getRestaurants();
  }

  sort(field: string): void {
    if (this.sortField === field) {
      this.sortOrder = this.sortOrder === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortField = field;
      this.sortOrder = 'asc';
    }
    this.getRestaurants();
  }
}
