import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { firstValueFrom } from 'rxjs';
import { UserService } from 'src/app/service/User/user.service';

@Component({
  selector: 'app-update-user',
  templateUrl: './update-user.component.html',
  styleUrls: ['./update-user.component.css']
})
export class UpdateUserComponent {
  user: any = {};
  imageError: string | null = null;
  profilePictureInvalid: boolean = false;

  constructor(private userService: UserService, private route: ActivatedRoute,private router:Router) {}

  async ngOnInit() {
    let userId = this.route.snapshot.paramMap.get('data');
    if(userId!=null){
      this.user=await firstValueFrom(this.userService.findbyId(parseInt(userId)));
    }
  }

  onFileChange(event: any): void {
    if (event.target.files.length > 0) {
      const file = event.target.files[0];

      const reader = new FileReader();
      reader.onload = () => {
        this.user.profilePicture = (reader.result as string).split(',')[1]; // Remove the base64 prefix
      };
      reader.readAsDataURL(file);
    }
  }

  onSubmit(): void {
    // this.userService.updateUser(this.user).subscribe(response => {
    //   alert('User updated successfully');
    // }, error => {
    //   console.error('Error updating user', error);
    //   alert('Failed to update user');
    // });

    this.router.navigate(['/admin/registration-requests']);
  }
}
