import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { User } from 'src/app/entities/User';
import { UserService } from 'src/app/service/User/user.service';

@Component({
  selector: 'app-guest',
  templateUrl: './guest.component.html',
  styleUrls: ['./guest.component.css']
})
export class GuestComponent {

registrationRequests: Array<User> = [];
  existingUsers: Array<User> = [];
  activeButton: string = '';
  constructor(private userService: UserService,private router:Router) { }



  selectComponent(component: string,userId:Number): void {
    if (this.activeButton === component) {
      this.activeButton = '';
      this.router.navigate(['/admin/registration-requests']);
    } else {
      this.activeButton = component;
      this.router.navigate([`admin/registration-requests/${component}`,{ data: userId ,return: '/admin/registration-requests'}]);
    }
  }
  selectComponent(component: string): void {
    if (this.activeButton === component) {
      this.activeButton = '';
      this.router.navigate(['/admin/registration-requests']);
    } else {
      this.activeButton = component;
      this.router.navigate([`admin/registration-requests/${component}`,{ return: '/admin/registration-requests'}]);
    }
  }
}
