import { Component } from '@angular/core';
import { UserService } from '../../service/user.service';

@Component({
  selector: 'app-admin-add-waiter',
  templateUrl: './admin-add-waiter.component.html',
  styleUrls: ['./admin-add-waiter.component.css']
})
export class AdminAddWaiterComponent {
  waiter = {
    name: '',
    surname: '',
    email: '',
    restaurantId: ''
  };

  constructor(private adminService: UserService) { }

  onSubmit(): void {
    this.adminService.addWaiter(this.waiter).subscribe(
      response => {
        alert('Waiter added successfully!');
        this.resetForm();
      },
      error => {
        console.error('Error adding waiter', error);
        alert('Failed to add waiter');
      }
    );
  }

  resetForm(): void {
    this.waiter = {
      name: '',
      surname: '',
      email: '',
      restaurantId: ''
    };
  }
}
