import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from 'src/app/service/user.service';

@Component({
  selector: 'app-change-password',
  templateUrl: './change-password.component.html',
  styleUrls: ['./change-password.component.css']
})
export class ChangePasswordComponent {
  step = 1;
  oldPassword!: string;
  newPassword!: string;
  confirmNewPassword!: string;
  username!: string;
  securityQuestion!: string;
  securityAnswer!: string;

  constructor(private changePasswordService: UserService, private router: Router) {}


  onSubmitOldPassword() {
    if (this.newPassword !== this.confirmNewPassword) {
      alert('New passwords do not match');
      return;
    }
    this.changePasswordService.changePasswordWithOldPassword(this.oldPassword, this.newPassword).subscribe(
      response => {
        alert('Password changed successfully');
        this.router.navigate(['/login']);
      },
      error => {
        console.error('Error changing password', error);
        alert('Failed to change password');
      }
    );
  }

  onSubmitUsername() {
    this.changePasswordService.getSecurityQuestion(this.username).subscribe(
      response => {
        // this.securityQuestion = response.securityQuestion;
        this.step = 3;
      },
      error => {
        console.error('Error fetching security question', error);
        alert('Failed to fetch security question');
      }
    );
  }

  onSubmitSecurityAnswer() {
    this.changePasswordService.verifySecurityAnswer(this.username, this.securityAnswer).subscribe(
      response => {
        this.step = 4;
      },
      error => {
        console.error('Error verifying security answer', error);
        alert('Failed to verify security answer');
      }
    );
  }

  onSubmitNewPassword() {
    if (this.newPassword !== this.confirmNewPassword) {
      alert('New passwords do not match');
      return;
    }
    this.changePasswordService.changePasswordWithSecurityAnswer(this.username, this.newPassword).subscribe(
      response => {
        alert('Password changed successfully');
        this.router.navigate(['/login']);
      },
      error => {
        console.error('Error changing password', error);
        alert('Failed to change password');
      }
    );
  }
}
