import { Component } from '@angular/core';

@Component({
  selector: 'app-admin-add-restaurant',
  templateUrl: './admin-add-restaurant.component.html',
  styleUrls: ['./admin-add-restaurant.component.css']
})
export class AdminAddRestaurantComponent {
  restaurant = {
    name: '',
    type: '',
    address: '',
    description: ''
  };

  constructor(private adminService: AdminService) { }

  onSubmit(): void {
    this.adminService.addRestaurant(this.restaurant).subscribe(
      response => {
        alert('Restaurant added successfully!');
        this.resetForm();
      },
      error => {
        console.error('Error adding restaurant', error);
        alert('Failed to add restaurant');
      }
    );
  }

  resetForm(): void {
    this.restaurant = {
      name: '',
      type: '',
      address: '',
      description: ''
    };
  }
}
