import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { User } from '../entities/User';
@Injectable({
  providedIn: 'root'
})
export class UserService {
  constructor(private http: HttpClient) { }
  private baseUrl = 'http://localhost:8080/users';

  login(username: string, password: string){
    const data={
      username: username,
      password: password
    }
    return this.http.post<User>("http://localhost:8080/users/login", data)
  }

  register(data:any){
    return this.http.post<User>("http://localhost:8080/users/register", data)
  }

  getLoginRequests(){
    return this.http.get<Array<User>>("http://localhost:8080/users/login-requests")
  }

  approveRequest(id: Number){
    return this.http.put<User>(`http://localhost:8080/users/approve-login-request/${id}`,null)
  }

  rejectRequest(id: Number){
    return this.http.delete<User>(`http://localhost:8080/users/reject-login-request/${id}`)
  }


  changePasswordWithOldPassword(oldPassword: string, newPassword: string)  {
    return this.http.post(`${this.baseUrl}/change-password/with-old-password`, { oldPassword, newPassword });
  }

  getSecurityQuestion(username: string) {
    return this.http.get(`${this.baseUrl}/change-password/security-question/${username}`);
  }

  verifySecurityAnswer(username: string, securityAnswer: string) {
    return this.http.post(`${this.baseUrl}/change-password/verify-security-answer`, { username, securityAnswer });
  }

  changePasswordWithSecurityAnswer(username: string, newPassword: string) {
    return this.http.post(`${this.baseUrl}/change-password/with-security-answer`, { username, newPassword });
  }
}
